mapper.insert:

insert into img_watermark (watermark_id, name, file_path, 
  alpha, fiducial_point, margin_top, 
  margin_bottom, sys_id, dealer_id, 
  user_id, last_modified_time, status
  )
values (#{watermarkId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{filePath,jdbcType=VARCHAR}, 
  #{alpha,jdbcType=TINYINT}, #{fiducialPoint,jdbcType=BIT}, #{marginTop,jdbcType=INTEGER}, 
  #{marginBottom,jdbcType=INTEGER}, #{sysId,jdbcType=VARCHAR}, #{dealerId,jdbcType=INTEGER}, 
  #{userId,jdbcType=INTEGER}, #{lastModifiedTime,jdbcType=TIMESTAMP}, #{status,jdbcType=BIT}
  )

mapper.select:
	SELECT  
	  
	 from img_watermark
	where 1=1
    
    and  name = #{name,jdbcType=VARCHAR}
    
    
    and  sys_id = #{sysId,jdbcType=VARCHAR}
    
    
    and dealer_id = #{dealerId,jdbcType=INTEGER}
    
    
    and user_id = #{userId,jdbcType=INTEGER}
    
    
    and status = #{status,jdbcType=BIT}
