mapper.select:
SELECT mpa.id, mpa.group_id, mpa.content, mpa.create_time,
mpal.id receiver_id,mpal.receiver_name,mpal.receiver_identity,mpal.read_state,mpal.read_time
FROM msg_push_app mpa LEFT JOIN msg_push_app_list mpal
ON mpa.id = mpal.push_id
WHERE mpal.receiver_identity = #{receiverIdentity,jdbcType=VARCHAR}
AND mpal.delete_state = 0 AND mpa.creator_type = #{creatorType,jdbcType=INTEGER}

  
        AND (UNIX_TIMESTAMP(mpa.create_time)*1000) < #{createTime}
        

ORDER BY mpa.create_time desc
limit 10

SELECT COUNT(1)
FROM msg_push_app mpa LEFT JOIN msg_push_app_list mpal
ON mpa.id = mpal.push_id
WHERE mpal.receiver_identity = #{receiverIdentity,jdbcType=VARCHAR}
AND mpal.delete_state = 0 AND mpal.read_state = 0

  AND mpa.creator_type = #{creatorType,jdbcType=INTEGER}
