mapper.select:
select 
pg.id, pg.product_id, pg.name,pg.group_supplier_id, pg.city_departure, pg.create_time,pg.group_setting
from product_group pg,product_group_seller pgs
where pg.`id`=pgs.`group_id` AND pg.product_id = #{productId,jdbcType=INTEGER}
and pg.state !=-1 AND pgs.operator_id=#{sellerId,jdbcType=INTEGER}
ORDER BY create_time DESC

	SELECT COUNT(*) FROM product_group WHERE product_id = #{productId,jdbcType=INTEGER} AND NAME=#{name,jdbcType=VARCHAR}
	AND state !=-1
	
    AND id != #{id,jdbcType=INTEGER}


SELECT pg.`id` groupId,pg.`name`,p.`price_settlement_adult` priceSettlementAdult,p.`price_settlement_child` priceSettlementChild,p.`price_cost_adult` priceCostAdult,p.`price_cost_child` priceCostChild
 FROM `product_group_supplier` supplier JOIN `product_group` pg ON supplier.id=pg.group_supplier_id JOIN product_group_price p ON pg.id=p.group_id
 WHERE supplier.product_id=#{productId}  AND supplier.supplier_id = #{supplierId} 
AND p.`group_date`<=#{groupDate} AND p.`group_date_to`>=#{groupDate}
AND supplier.state=1 AND pg.state=1
ORDER BY supplier.create_time
