mapper.update:
update taobao_stock_product
set product_id = #{productId,jdbcType=INTEGER},
  stock_id = #{stockId,jdbcType=INTEGER},
   product_skus_id = #{productSkusId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, product_id, stock_id ,product_skus_id
from taobao_stock_product
where stock_id = #{stockId}

select id, product_id, stock_id 
from taobao_stock_product
where stock_id = #{stockId,jdbcType=INTEGER} and product_id=#{productId}

select id, product_id, stock_id ,product_skus_id
from taobao_stock_product
where stock_id = #{stockId,jdbcType=INTEGER} and product_skus_id=#{productSkusId}
