mapper.select:
SELECT sum(num_sold) as num_sold,sum(num_stock) as num_stock 
FROM traffic_res_product 
where res_id=#{resId}

mapper.update:
	update traffic_res_product
	set  num_stock = #{numStock,jdbcType=INTEGER}
 	where id = #{id,jdbcType=INTEGER}

mapper.select:
select id,product_name,product_code from (
SELECT trp.id,trp.product_name,trp.product_code
,ifnull((select count(*) from product_group_supplier aa where aa.group_id=trp.res_id and aa.product_id=trp.product_code),0) as supplierCount
,ifnull((select count(*) from product_group_supplier aa where aa.group_id=trp.res_id and aa.product_id=trp.product_code and aa.supplier_id=#{page.parameter.supplierId}),0) as supplierCountMy
FROM traffic_res_product AS trp
LEFT JOIN traffic_res AS tr ON(trp.res_id=tr.id)
WHERE tr.state = 1 AND trp.product_code > 0 AND tr.date_latest >= now()
AND tr.biz_id = #{page.parameter.bizId,jdbcType=INTEGER}
AND tr.date_start >= #{page.parameter.startTime}
AND tr.date_start <= #{page.parameter.endTime}
) pp where (supplierCountMy > 0 or supplierCount=0)	GROUP BY pp.product_code

SELECT trp.id,trp.res_id,tr.date_start,trp.num_stock,trp.num_sold,sale.unconfirm,sale.confirm,tr.num_balance
FROM traffic_res_product AS trp
LEFT JOIN traffic_res AS tr ON(trp.res_id=tr.id)
LEFT JOIN (SELECT res_product_id
,ifnull(sum(CASE WHEN adjust_action='ORDER_SOLD' AND adjust_state=0 THEN adjust_num ELSE 0 END ),0) unconfirm
,ifnull(sum(CASE WHEN adjust_action='ORDER_SOLD' AND adjust_state=1 THEN adjust_num ELSE 0 END),0) confirm
FROM traffic_res_stocklog WHERE order_id > 0 GROUP BY res_product_id
) AS sale ON sale.res_product_id=trp.id
WHERE tr.state = 1 AND trp.product_code > 0 AND tr.date_latest >= now() AND tr.biz_id = #{bizId,jdbcType=INTEGER}
AND tr.date_start >= #{startTime} AND tr.date_start <= #{endTime}
AND trp.product_code = #{productCode}
ORDER BY date_start
