mapper.select:
SELECT * FROM plat_auth 
WHERE plat_status = 1 AND org_id > 0 
AND biz_id = #{bizId,jdbcType=INTEGER}

SELECT * FROM plat_auth 
WHERE plat_status = 1 AND biz_id = #{bizId,jdbcType=INTEGER} 

      AND org_id = #{orgId,jdbcType=INTEGER}


AND supplier_id = #{supplierId,jdbcType=INTEGER}
