mapper.update:

	update sys_data_right_supplier
	set 
		org_id=#{orgId},
		biz_id=#{bizId},
		supplier_id=#{supplierId}
	where id = #{id}

mapper.select:
		
		 
			select distinct(a.supplier_id) from sys_data_right_supplier a 
			where a.biz_id =#{param.parameter.bizId} 
		

		
			
				and a.org_id in 
			
			
				#{orgid}

		 
			select distinct(a.supplier_id) from sys_data_right_supplier a 
			where a.biz_id =#{bizId} 
		

		
			
				and a.org_id in 
			
			
				#{orgid}

 
	select a.supplier_id from sys_data_right_supplier a 
	 where 
	a.org_id = #{orgid} and a.biz_id = #{bizId}
